import './App.css';
import {Routes, Route} from 'react-router-dom';
import Header from '../Header/Header';
import Footer from '../Footer/Footer';
import Main from '../Main/Main';
import Login from '../Login/Login';
import Register from '../Register/Register';
import Profile from '../Profile/Profile';
import NotFound from '../NotFound/NotFound';
import Movies from '../Movies/Movies';
import MainApi from "../../utils/MainApi";
import {useState, useEffect} from 'react';

function App() {
  // стэйт пользователя — вошёл он в систему или нет
  const [loggedIn, setLoggedIn] = useState(true);
  const [savedCards, setsavedCards] = useState([]);

  function isSavedCard(card) {
    return savedCards.some((item) => item.id === card.id);
  }

  function onBookmarkClick(card, isMarked) {
    if (isMarked) {
      addMovie(card);
    } else {
      //deleteMovie(card);
    }
  }

  function addMovie(movie) {
    MainApi
      .createMovie(movie)
      .then((res) => {
        setSavedMovies([...savedMovies, { ...res, id: res.movieId }]);
      })
      .catch(() => {
        /*setIsPopupOpen(true);
        handlePopupContent({
          iconPath: failLogoPath,
          text: "На сервере произошла ошибка",
        });
        setTimeout(closeAllPopups, 2500);*/
      });
  }

  return (
    <div className="App">

      <Header>

      </Header>
      <Routes >

        <Route exact path='/sign-up' element = {<Register />} />
        <Route exact path='/sign-in' element = {<Login />} />
        <Route exact path='/profile' element = {<Profile />} />
        <Route exact path='/movies/*' element = {<Movies isSavedCard= {isSavedCard} onBookmarkClick={onBookmarkClick}/>} />

        <Route exact path='/404' element = {<NotFound />} />
        <Route exact path='/' element = {
          <Main />

        }/>
      </Routes>

      <Footer />
    </div>
  );
}

export default App;
