import React from 'react';
import {useState} from 'react';
import {Link} from 'react-router-dom';
import logo from '../../images/logo.svg';

function Register ({registration}) {
    
    // Стейт, в котором содержится значение инпута - mail, password
    const [name, setName] = useState('');
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');

    // Обработчики изменения инпута обновляют стейт

    function handleChangeName(e) {
        setName(e.target.value);
    }

    function handleChangeEmail(e) {
        setEmail(e.target.value);
    }

    function handleChangePassword(e) {
        setPassword(e.target.value);
    }

//Отправляем данные на сервер
    function handleSubmit(e) {
    // Запрещаем браузеру переходить по адресу формы
    e.preventDefault();
    
    // Передаём значения управляемых компонентов во внешний обработчик
    const datastartPage = {};
    datastartPage.email = email;
    datastartPage.password = password;
    registration(datastartPage);
    }

    return (
        <div className="Login__container">
            <img className="form__logo" src={logo} alt="логотип Улыбка"/>
            <h2 className="Login__title">Добро пожаловать!</h2>
            <form name ={'Регистрация'} className="Login__form" onSubmit={handleSubmit}>
                
                <div className="Login__label">
                    <h2 className='Login__name'>Имя</h2>
                    <input
                        value={name || ''} onChange={handleChangeName}
                        required minLength="1" maxLength="30" type="text"
                        name ="Имя"
                        className="Login__input Login__input_text_namePlace"
                    />
                    <span className="Login__input-error"></span>
                </div>

                <div className="Login__label">
                    <h2 className='Login__name'>E-mail</h2>
                    <input
                        value={email || ''} onChange={handleChangeEmail}
                        required minLength="1" maxLength="30" type="text"
                        name ="Email" placeholder = "Email"
                        className="Login__input Login__input_text_namePlace"
                    />
                    <span className="Login__input-error"></span>
                </div>

                <div className="Login__label">
                    <h2 className='Login__name'>Пароль</h2>
                    <input 
                        value={password || ''} onChange={handleChangePassword}
                        required type="password" name ="password"
                        placeholder = "Пароль" className="Login__input Login__input_text_link"
                    />
                    <span className="Login__input-error"></span>
                </div>

                <button type ="submit" aria-label="saveButton"
                    className="Login__button Login__button_invalid">Зарегистрироваться
                </button>
                <p className="Login__button_span">Уже зарегистрированы?
                    <Link className="Login__button_link" to='/sign-in'> Войти</Link>
                </p>
                
            </form>
        </div>
    )
}

export default Register;
