import React from 'react';
import MainStack from '../MainStack/MainStack';

function Portfolio () {

    const text = {
        titleName1: 'Александр',
        titleName2: 'Фронтенд-разработчик, 30 лет',
        description1: 'Я родился и живу в Саратове, закончил факультет экономики СГУ. У меня есть жена и дочь. Я люблю слушать музыку, а ещё увлекаюсь бегом. Недавно начал кодить. С 2015 года работал в компании «СКБ Контур». После того, как прошёл курс по веб-разработке, начал заниматься фриланс-заказами и ушёл с постоянной работы.'
    }

    const AboutMe = {
        width: 1140,
        flexDirection: 'row',
        marginLeft: 'auto',
        marginRight: 'auto',
        border: '1px solid rgba(255, 44, 44, 0.7)'
    }

    const AboutMe__title = {
        /*width: 550,*/
        display: 'flex',
        flexDirection: 'row',
        /*marginRight: 40,*/
        fontStyle: 'normal',
        fontWeight: 400,
        fontSize: 20,
        /*lineHeight: 24,*/
        letterSpacing: '-0.04em',
        color: '#000000',
        border: '1px solid rgba(255, 44, 44, 0.7)'
    }

    const AboutMe__description = {
            width: 600,
            display: 'flex',
            flexDirection: 'row',
            /*marginRight: 40,*/
            fontStyle: 'normal',
            fontWeight: 400,
            fontSize: 14,
            /*lineHeight: 22,*/
            color: '#000000',
            /*border: '1px solid rgba(255, 44, 44, 0.7)'*/
    }

    return (
        <div>
            <MainStack stackName = {'Студент'} id ='Студент'>
                <div>
                    <div style= {AboutMe} >
                        <p style={AboutMe__title}>{text.titleName1}</p>
                        <p style={AboutMe__title}>{text.titleName2}</p>
                    </div>
                    <div style= {AboutMe}>
                        <p style={AboutMe__description}>{text.description1}</p>
                    </div>
                </div>
            </MainStack>
        </div>
    );
}

export default Portfolio;
