import React from 'react';
import { useState } from 'react';
import {Link} from 'react-router-dom';
import FormList from '../FormList/FormList';
import FormComponent from '../FormComponent/FormComponent';

function Profile () {
    // Стейт, в котором содержится значение инпута - mail, password
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');

    // Обработчики изменения инпута обновляют стейт

    function handleChangeEmail(e) {
        setEmail(e.target.value);
    }

    function handleChangePassword(e) {
        setPassword(e.target.value);
    }

    //Отправляем данные на сервер
    function handleSubmit(e) {
        // Запрещаем браузеру переходить по адресу формы
        e.preventDefault();
        // Передаём значения управляемых компонентов во внешний обработчик
        authorization({ email, password });

    }

    //очищаем инпуты при каждом открытии модального окна
    /*useEffect(() => {
        if (isOpen) {
            setMail('');
            setPassword('')
        }
    }, [isOpen])*/

    return (
        <div>
            <div className="FormList__container">
                <h2 className="Formlist__title">Привет, Виталий!</h2>

                <form name ={'Регистрация'} onSubmit={onSubmit}>
                    <div className="FormList__form">
                    
                    </div>
                    
                </form>
            </div>
        </div>

        
    )

}

export default Profile;
