import React from 'react';
import { useState } from 'react';

function Login({authorization}) {
    // Стейт, в котором содержится значение инпута - mail, password
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');

    // Обработчики изменения инпута обновляют стейт
    function handleChangeEmail(e) {
        setEmail(e.target.value);
    }

    function handleChangePassword(e) {
        setPassword(e.target.value);
    }

    //Отправляем данные на сервер
    function handleSubmit(e) {
        // Запрещаем браузеру переходить по адресу формы
        e.preventDefault();
        // Передаём значения управляемых компонентов во внешний обработчик
        authorization({ email, password });

    }

    //очищаем инпуты при каждом открытии модального окна
    /*useEffect(() => {
        if (isOpen) {
            setMail('');
            setPassword('')
        }
    }, [isOpen])*/

    return (

        <div className="Login__container">
            <h2 className="Login__name">{'Добро пожаловать!'}</h2>
                    <form name ={'Вход'} className="Login__form" onSubmit={handleSubmit}>
                        <div className="Login__label">
                        <h2>{'Email'}</h2>
                        <input
                            value={email || ''} onChange={handleChangeEmail}
                            required minLength="1" maxLength="30" type="text"
                            name ="Email"
                            className="Login__input Login__input_text_namePlace"
                        />
                        <span className="Login__input-error"></span>
                        
                        </div>
                        <div className="Login__label">
                            <input 
                                value={password || ''} onChange={handleChangePassword}
                                required type="password" name ="password"
                                placeholder = "Пароль" className="Login__input Login__input_text_link"
                            />
                            <span className="Login__input-error"></span>
                        </div>
                        <button type ="submit" aria-label="saveButton"
                            className="Login__button Login__button_invalid">Войти
                        </button>
                    </form>
        </div>
    )

}

export default Login;
