import React from 'react';
import MainStack from '../MainStack/MainStack';

function Portfolio () {

    const text = {
        titleName1: 'Александр',
        titleName2: 'Фронтенд-разработчик, 30 лет',
        description1: 'Я родился и живу в Саратове, закончил факультет экономики СГУ. У меня есть жена и дочь. Я люблю слушать музыку, а ещё увлекаюсь бегом. Недавно начал кодить. С 2015 года работал в компании «СКБ Контур». После того, как прошёл курс по веб-разработке, начал заниматься фриланс-заказами и ушёл с постоянной работы.'
    }

    return (
        <div>
            <MainStack stackName = {'Студент'} id ='Студент'>
            <section className="profile">
                    <div className="profile__space">
                        <div className = "profile__holder">
                            <div className = "profile__round">
                                <img className="profile__avatar" src={currentUser.avatar} alt="Аватар пользователя"/>
                                <button type ="button" aria-label="edit" onClick={handleEditAvatarClick} 
                                    className="profile__avatar-button">
                                </button>
                            </div> 
                        </div>    
                            <div className="profile__info">
                            <h1 className="profile__title">{currentUser.name}</h1>
                            <button type ="button" aria-label="edit" onClick={handleEditProfileClick}
                            className="profile__edit-button opacity-buttons"></button>
                            <p className="profile__subtitle">{currentUser.about}</p>
                        </div>
                    </div>
                    <button type ="button" aria-label="add" onClick={handleAddPlaceClick}
                    className="profile__add-button opacity-buttons"></button>
                </section>
            </MainStack>
        </div>
    );
}

export default Portfolio;
