import React from 'react';

const ProgressBar = ({ completeColor, completed }) => {


    const fillerStyles = {
        width: `${completed}%`,
        backgroundColor: completeColor,
        extAlign: 'center',
        justifyContent: 'center',
        maxWidth: 1140,
        minWidth:0
    }
    
    const fillerStyles2 = {
        width: `${'100' - completed}%`,
        overflow: 'hidden',
        textAlign: 'center',
        justifyContent: 'center',
        maxWidth: 1140,
        minWidth:0
    }

    return (
        <div className="ProgressBar">
            <div style={fillerStyles}>
                <p className="ProgressBar__text">{`${'1 неделя'}`}</p>
            </div>
            <div style={fillerStyles2}>
                <p className="ProgressBar__text">{`${'4 недели'}`}</p>
            </div>
        </div>
    );
};

export default ProgressBar;
