import React from 'react';

const ProgressBar = ({ completeColor, completed }) => {

    /*lamp = (min, value, max) => {
        return Math.min(Math.max(min, value), max);
    }*/

    const fillerStyles = {
        width: `${completed}%`,
        minWidth: `${'0'}%`,
        maxWidth: `${'100'}%`,
        backgroundColor: completeColor,
    }
    
    const fillerStyles2 = {
        width: `${'100' - completed}%`,
        minWidth: `${'0'}%`,
        maxWidth: `${'100'}%`,
    }

    return (
        <div className="ProgressBar">
            <div style={fillerStyles}>
                <span className="ProgressBar__text">{`${'1 неделя'}`}</span>
            </div>
            <div style={fillerStyles2}>
                <span className="ProgressBar__text">{`${'4 недели'}`}</span>
            </div>
        </div>
    );
};

export default ProgressBar;
