import React from 'react';

const ProgressBar = ({ completeColor, completed, titleBar1, titleBar2 }) => {


    const fillerStyles = {
        width: `${completed}%`,
        backgroundColor: completeColor,
    }
    
    const fillerStyles2 = {
        width: `${'100' - completed}%`,
    }

    return (
        <div className="ProgressBar">
            <div style={fillerStyles}>
                <p className="ProgressBar__text">{titleBar1}</p>
                <span className="ProgressBar__text">'Back'</span>
            </div>
            <div style={fillerStyles2}>
                <p className="ProgressBar__text">{titleBar2}</p>
                <span className="ProgressBar__text">'Front'</span>
            </div>
        </div>
    );
};

export default ProgressBar;
