import MoviesCard from '../MoviesCard/MoviesCard';
import CurrentUserContext from '../../contexts/CurrentUserContext';
import {useState, useEffect} from 'react';
import {useContext} from 'react';

function Movies(
{onCardClick, onCardLike, onCardDelete}) {

    const currentUser = useContext(CurrentUserContext);
    //console.log ({cards});
    //console.log ([cards]);

    const apiURL = 'https://api.nomoreparties.co/beatfilm-movies'

    const [movies, setMovies] = useState([]);

    const updateMovies = (movies) => {
        setMovies(movies)
        localStorage.setItem('all_movies', JSON.stringify(movies));
    }

    useEffect (() => {
        const movies = JSON.parse(localStorage.getItem('all_movies') || '[]')
        if (!movies.length) {
        fetch (apiURL, {
            headers: {
            'ContentType': 'aplication/json'
            }
        })
        .then((res) => res.json)
        .then((res) => {
            updateMovies(res)
        });
        } else {
        setMovies(movies);
        }
    }, []);

    return (
        <main>
            <div className="page__container">
                
                <section className="elements">
                    {movies.map (movie => {
                        return <MoviesCard 
                            onCardClick = {onCardClick} onCardLike = {onCardLike}
                            onCardDelete ={onCardDelete} card={movie} key = {movie._id}/>
                    })}
                </section>
            </div>
        </main>
    );
}

export default Movies;
